mapper.select:
select id,mes_sender_id as mesSenderId,mes_object_id as mesObjectId, mes_type as mesType,mes_contents as mesContents, mes_status as mesStatus,create_time as createTime, mes_sender_type as mesSenderType, mes_object_type as mesObjectType from c_message where mes_object_type=#{mesObjectType}
and mes_object_id =#{mesObjectId}
and mes_status != 2

select user_name
from c_users
where  id=#{mesSenderId}

select tea_name
from c_teacher
where  id=#{mesSenderId}

mapper.update:
update c_message
    SET mes_status=2
    WHERE id=#{mesId}
